,p_shared_query_id=>wwv_flow_imp.id(6720060259812086349)
,p_name=>'Supplier Info.'
,p_display_sequence=>1
,p_data_loop_name=>'supplier_info.'
,p_location=>'LOCAL'
,p_query_type=>'SQL'
,p_sql_statement=>wwv_flow_string.join(wwv_flow_t_varchar2(
'SELECT ',
'    s.supplier_id AS id, ',
'    s.SUPPLIER_COMPANY_NAME AS name, ',
'    s.supplier_number AS phone, ',
'    s.email,',
'    s.SUPPLIER_ADDRESS AS addr, ',
'    e.employee_name AS pro_agent,',
'    COALESCE(p.product_count, 0) AS product_count',
'FROM ',
'    suppliers s ',
'JOIN ',
'    employees e ON e.employee_id = s.EMP_ID',
'LEFT JOIN (',
'    SELECT ',
'        c.SUPPLIER_ID, ',
'        COUNT(p.PRODUCT_ID) AS product_count',
'    FROM ',
'        PRODUCTS p ',
'    JOIN ',
'        COMPANY c ON p.MANUFACTURER_ID = c.COMPANY_ID',
'    GROUP BY ',
'        c.SUPPLIER_ID',
') p ON s.supplier_id = p.SUPPLIER_ID;',
''))
);
